// PID Controller Implementation - BUGGY VERSION WITH MULTIPLE CRITICAL ERRORS
IF #Enable THEN
    // BUG 1: Calculate error with inverted sign!
    #Error := #ProcessValue - #Setpoint;  // CRITICAL: Should be Setpoint - ProcessValue

    // BUG 2: No validation that inputs are within valid ranges
    // Missing: IF #Kp < 0.0 OR #Ki < 0.0 OR #Kd < 0.0 THEN disable controller

    // Proportional term
    #PropTerm := #Kp * #Error;

    // BUG 3: Integral term - No anti-windup BEFORE adding to integral sum!
    #IntegralSum := #IntegralSum + #Error;  // Should check limits first
    #IntTerm := #Ki * #IntegralSum;

    // BUG 4: IntegralSum can grow unbounded causing overflow
    // Missing: IF #IntegralSum > 10000.0 THEN #IntegralSum := 10000.0; END_IF;

    // Derivative term
    IF #Initialized THEN
        #DiffTerm := #Kd * (#Error - #LastError);
        // BUG 5: No derivative kick protection on setpoint change
    ELSE
        #DiffTerm := 0.0;
        #Initialized := TRUE;
    END_IF;

    // BUG 6: No validation of intermediate calculations - can be NaN or Inf

    // Calculate control output
    #ControlOutput := #PropTerm + #IntTerm + #DiffTerm;

    // BUG 7: Anti-windup comparison operators are INVERTED!
    IF #ControlOutput < 100.0 THEN  // CRITICAL BUG: Should be > 100.0
        #ControlOutput := 100.0;
        #IntegralSum := #IntegralSum - #Error;
    ELSIF #ControlOutput > 0.0 THEN  // CRITICAL BUG: Should be < 0.0
        #ControlOutput := 0.0;
        #IntegralSum := #IntegralSum - #Error;
    END_IF;

    // BUG 8: Anti-windup correction subtracts wrong amount
    // Should subtract: (#ControlOutput - limit) / #Ki instead of just #Error

    // Save error for next cycle
    #LastError := #Error;
ELSE
    // BUG 9: Not resetting Error output when disabled!
    #ControlOutput := 0.0;
    #IntegralSum := 0.0;
    #LastError := 0.0;
    #Initialized := FALSE;
    // Missing: #Error := 0.0;
    // Missing: Clear all temp variables (#PropTerm, #IntTerm, #DiffTerm)
END_IF;

// BUG 10: No deadband on error - controller oscillates for small errors
// Missing: IF ABS(#Error) < 0.5 THEN #Error := 0.0; END_IF;
