// Motor Control Logic - BUGGY VERSION WITH MULTIPLE CRITICAL ERRORS

// BUG 1: Logic inversion - Stop button turns motor ON instead of OFF
IF #Start THEN
    #Motor_On := TRUE;
END_IF;

IF #Stop THEN
    #Motor_On := TRUE;  // CRITICAL BUG: Should be FALSE
END_IF;

// BUG 2: Race condition - Start and Stop can both be TRUE simultaneously
// Missing mutual exclusion check: IF #Start AND #Stop THEN handle conflict

// BUG 3: No emergency stop override
// Missing: IF #Emergency_Stop THEN #Motor_On := FALSE; RETURN; END_IF;

// BUG 4: Missing upper speed limit check - can exceed safe maximum (4000 RPM)
IF #Motor_On THEN
    #temp_speed := #Speed_Setpoint;

    // BUG 5: No validation that Speed_Setpoint is within safe range (0-4000 RPM)
    // Missing: IF #temp_speed > 4000.0 OR #temp_speed < 0.0 THEN #temp_speed := 0.0; END_IF;

    // Ramp up speed
    IF #Current_Speed < #temp_speed THEN
        #Current_Speed := #Current_Speed + 10.0;

        // BUG 6: No maximum speed safety limit enforced!
        IF #Current_Speed > #temp_speed THEN
            #Current_Speed := #temp_speed;
        END_IF;
        // Missing absolute safety limit: IF #Current_Speed > 4000.0 THEN #Current_Speed := 4000.0; END_IF;
    END_IF;

    // BUG 7: No overspeed protection - if speed exceeds limit, should trigger emergency stop

ELSE
    // BUG 8: Missing ramp down - immediate stop from high speed is mechanically dangerous!
    #Current_Speed := 0.0;  // Should ramp down: IF #Current_Speed > 10.0 THEN #Current_Speed := #Current_Speed - 20.0;
END_IF;

// BUG 9: No state validation - Current_Speed can be negative after arithmetic errors
// Missing: IF #Current_Speed < 0.0 THEN #Current_Speed := 0.0; END_IF;
